mapper.select:
select count(*)
from cart_item


        and cart_item_id = #{cartItemId}


        and user_id = #{userId}


        and book_id = #{bookId}


        and book_number = #{bookNumber}


        and cart_item_status = #{cartItemStatus}


        and create_time = #{createTime}


        and updata_time = #{updataTime}
